import React, {Component} from 'react'
import axios from 'axios'
import Cartlist from './Cartlist'
import Table from 'react-bootstrap/Table'
import back1 from './UI/back1.PNG'
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

class Cart extends Component { 
     
     handleClick() {
        console.log('Button click ...');
        localStorage.removeItem("items");
        localStorage.removeItem("category")
        localStorage.removeItem("total")
        window.location.reload(1000);
      }

     handle() {
         console.log("clicked");
         axios.post('http://localhost:5000/order/add',
         {
            "catogary": localStorage.category,
            "subcatogary": "North Indian",
            "product": JSON.parse(localStorage.getItem('items')),
            "paymentType": "cash",
            "totalCost": localStorage.total
         },
         {
             headers: {
               'auth-token': localStorage.usertoken
             }},
             )
             .then(res => {
                 if(res.data.status==true){
                    toast(res.data.data, {position: toast.POSITION.TOP_CENTER});
                    var interval = setInterval(
                        function()
                        {  
                                console.log(res.data.redirect);
                                axios.get(res.data.redirect)
                                .then(res => {
                                    if(res.data.status==true){
                                       clearInterval(interval);
                                       console.log(res.data.data)
                                    }
                                })
                                .catch(err => {
                                    console.log("err")
                                })
                        }, 30000);
                 }
                 else{
                     alert(res.data.error)
                 }
             })
             .catch(err => {
                 console.log("err")
             })
       }

   

     render() {
        toast.configure()
        let arr
        if(localStorage.getItem('items')==undefined){
            // alert('cart is empty!!');
            toast("Cart is empty!!", {position: toast.POSITION.TOP_CENTER});
        }
        else
        {
            arr = JSON.parse(localStorage.getItem('items')).map( (item, i) => {
	
                return <Cartlist 
                         id={item.itemid}
                         cat={item.quantity}
                         price={item.totalprice} 
                    />
           
            } )
        }
        
        
        

     return (
        
         <div className="re" style={{border:'solid',borderColor:'white',backgroundImage:`url(${back1})`}}>
         <h2>Cart</h2>
         <div style={{marginLeft:'0.5%', marginTop:'1%',marginRight:'0.5%',marginBottom:'15%'}}>
         <h3 style={{textAlign:'left',marginLeft:'0.5%'}}> Selected Items :  </h3>
         
            
         
         <div style={{border:'solid', borderRadius:'10px',marginLeft:'0.5%',marginRight:'0.5%', backgroundColor:'white'}}>
            <Table >
                 <thead>
                     <tr style={{width:'90%'}}>
                     <th style={{width:'30%'}}>Item</th>
                     <th style={{width:'30%'}}>quantity</th>
                     <th style={{width:'30%'}}>Price</th>
                     </tr>
                 </thead>
            </Table>
            {arr}
            <p style={{textAlign:'right', marginRight:'15%'}}><b>Total Amount : {localStorage.total}</b></p> 
         </div>
         <div style={{marginLeft:'5%',marginRight:'5%',marginTop:'3%', marginBottom:'18%'}}>
         <p><button type="button" className='btn btn-primary' onClick={this.handle} variant="secondary" style={{float:'right', borderRadius:'10px'}}>Place Order</button> <button className='btn btn-primary' type="button" onClick={this.handleClick} variant="secondary" style={{float:'right', borderRadius:'10px', marginRight:'0.5%'}}>Remove</button></p>
         </div>
         </div>
         </div>
     )
     }
 }
  

 export default Cart;
