function Arraylis (props)  {


    var counter = 1
    let total = 0

    toast.configure()
    function increment() {
          counter= counter + 1
          console.log(counter);
        //   alert("No of selected items"+ counter)
          toast(counter + " Items selected", {position: toast.POSITION.TOP_CENTER, autoClose: 1000});

      }

