import React, { Component } from 'react';
import { GoogleComponent } from 'react-google-location' 
import axios from 'axios'
import back1 from './UI/food.jpg'
 

class Maps extends Component {
  constructor(props) {
    super(props)
    this.state = {
      latitude: null,
      longitude: null,
      Address: null,
      key: 'AIzaSyB4TESW6B2tp03k08vStngbRHltlNC2O4A'
    };
    this.getLocation = this.getLocation.bind(this);
    this.getCoordinates = this.getCoordinates.bind(this);
    this.reverseGeocodeCoordinates = this.reverseGeocodeCoordinates.bind(this);
  }

  handleClick() {
    console.log(localStorage.latitude);
    // axios.post('http://localhost:5000/users/updatelatlong',
    axios.post('users/updatelatlong',
          {
            longi: localStorage.longitude,
            lat: localStorage.latitude
          }
          ,{
          headers: {
            'auth-token': localStorage.usertoken
          }},
          
          )
        .then(res => {
            alert('Location updated!')
        })
        .catch(err => {
            console.log("err")
        })
  }

  getLocation () {
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(this.getCoordinates);
    } else {
      alert("Geolocation is not supported by this browser.");
    }
  }

  getCoordinates (position) {
     this.setState({
      latitude: position.coords.latitude,
      longitude: position.coords.longitude,
    })
    this.reverseGeocodeCoordinates()
  }

  reverseGeocodeCoordinates() {
    fetch(`https://maps.googleapis.com/maps/api/geocode/json?latlng=${this.state.latitude},${this.state.longitude}&sensor=false&key=${this.state.key}`)
    .then(response => response.json())
    .then(data => console.log(data))
    .catch(error => alert(error))
  }

  render() {
    localStorage.setItem("latitude",this.state.latitude)
    localStorage.setItem("longitude",this.state.longitude)
    return (
      <div style={{ paddingTop:'1%', paddingBottom:'1%'}}>
      <div style={{ marginTop:'1%', marginBottom:'1%', marginLeft:'10%', marginRight:'10%'}}>
        <p style={{marginLeft:'20%'}}><b>Update Location</b></p>
        <button className='btn btn-primary' onClick={this.getLocation} style={{marginLeft:'20%'}}>Get location</button>
        <p name="latitude" style={{marginLeft:'20%'}}>Latitute : {this.state.latitude}</p>
        <p name="longitude" style={{marginLeft:'20%'}}>Longitude : {this.state.longitude}</p>
        <div style={{marginBottom:'3%'}}>
        <button className='btn btn-primary' type="button" style={{marginLeft:'20%'}} onClick={this.handleClick}>Update Location</button>
        </div>
      </div>
      </div>

    )
  } 
}


export default Maps;
