import React, {Component} from 'react' 
import axios from 'axios'
// import Accountlist from './Accountlist'
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

class Account extends Component {
    
    constructor(){
        super();
        this.state = {
            items: [],
        }
    }


    componentDidMount() {
        toast.configure()
        axios.get('http://localhost:5000/users/profile', {
        // axios.get('users/profile', {
            headers: {
              'auth-token': localStorage.usertoken
            }})
         .then(res => {
             console.log(res)
             console.log(res.data.data)
             if(res.data.status==true){
                this.setState({items: res.data.data})
            }
            else if(res.data.status==false){
                // alert(res.data.error)
                toast(res.data.error, {position: toast.POSITION.TOP_CENTER, autoClose: 1000});
            } 
         }
         )
         .catch(error => {
            // alert("login first");
            toast("Login First!!", {position: toast.POSITION.TOP_CENTER, autoClose: 1000});
        })
    }

    render() {

        
	
            // return <Accountlist
            //          name={this.state.items.name}
            //          username={this.state.items.username}
            //          address={this.state.items.address} 
            //          email={this.state.items.email}	
            //     />
        
 
        
        
        return (
            <div>
                <div className="row no-gutters ">
                    <div className="container text-left" style={{backgroundColor:'white', marginTop:'3%', paddingBottom:'2%'}}>
                        <h4 className="mt-2"><b>My Profile</b></h4>
                        <hr />
                        {
                        <form id="form" onSubmit={this.handleSubmit}>
                            <hr />
                            <div className="container">
                            <div className="form-row my-2">
                                <div className="col-sm-6">
                                Username:
                                <input
                                    readOnly
                                    type="text"
                                    name="username"
                                    id="username"
                                    className="form-control"
                                    placeholder={this.state.items.name}
                                />
                                </div>
                                <div className="col-sm-6">
                                Address:
                                <input
                                    readOnly
                                    type="text"
                                    name="address"
                                    id="address"
                                    placeholder={this.state.items.address}
                                    className="form-control"
                                />
                                </div>
                            </div>
                            <div className="form-row my-2">
                                <div className="col-sm-6">
                                Email:
                                <input
                                    readOnly
                                    type="email"
                                    className="form-control"
                                    id="email"
                                    name="email"
                                    placeholder={this.state.items.email}
                                />
                                </div>
                                <div className="col-sm-6">
                                Username:
                                <input
                                readOnly
                                        maxLength="10"
                                        type="text"
                                        name="phonenumber"
                                        id="phonenumber"
                                        placeholder={this.state.items.username}
                                        className="form-control"
                                />
                                </div>
                            </div>
                            <div className="form-row my-2">

                            </div>
                            </div>
                            {/* <div className="text-right">
                            <button
                                type="button"
                                id="editButton"
                                className="btn btn-secondary"
                                onClick={this.editform}
                                style={{marginRight:'1%'}}
                            >
                                Edit
                            </button>
                            </div> */}
                        </form>
                        }
                    </div>
      </div>

            </div>
           
        )
    }
    
}
    



export default Account;
