import React, {Component} from 'react'
import Arraylis from './Arraylis'  
import axios from 'axios'
import tp1 from './pics/thai/tp1.jpg'
import tp2 from './pics/thai/tp2.jpg'
import tp3 from './pics/thai/tp3.jpg'
import tp4 from './pics/thai/tp4.jpg'
import tp5 from './pics/thai/tp5.jpg'


class Vtab5 extends Component {
    
    state = {
        items: [],
    };
    
    componentDidMount() {
        axios.get('http://localhost:5000/menu/Thai')
        // axios.get('menu/Thai')
         .then(res => {
             console.log(res)
             this.setState({items: res.data})
             console.log(res.data)
         })
         .catch(error => {
             console.log(error)
         })
    }
    
    
    render() {

        // const images = [
        //     { id: 1, src: tp1 },
        //     { id: 1, src: tp2 },
        //     { id: 1, src: tp3 },
        //     { id: 1, src: tp4 },
        //     { id: 1, src: tp5 },
        //   ];
        
        const images = [
            { src: tp1 },
            { src: tp2 },
            { src: tp3 },
            { src: tp4 },
            { src: tp5 },
          ];
        
        // const arr = this.state.items.map( (item, index) => {
            
        //     return <Arraylis 
        //              itemId={item.products.itemId}
        //              catogary={item.products.catogaryName}
        //              subcategaryName={item.products.subcatogaryName}
        //              price={item.products.price} 
                            
        //         />
        
        // } )

        // const ar = images.map( (sr,id) => {
	
        //         return <Arraylis 
        //              id={sr.id}	
        //              src={sr.src}
        //         />
        
        // } )

        return (
            <div className="main-footer" style={{ marginTop:'3%', height:'auto', width:'auto' }}>
            <div className="container" style={{ height:'auto', width:'auto' }}>
                <div className="row" style={{marginTop:'3%', height:'auto', width:'auto'}}>
                {/* {ar} */}

                { this.state.items.map( (item, index) => 
                <div className="col-md-2 col-sm-4" style={{ height:'auto',padding:'0.5%', marginRight:'0.5%', marginLeft:'0.5%'}}>
                <div className="item1" style={{backgroundColor:'white', border:'solid', borderWidth:'1px', borderColor:'#9C9C9B'}}>
                <img src={images[index].src} style={{width:'100%', height:'70%'}}/>
                <p style={{margin:'0%'}}><b>{item.products.itemId}</b></p>
                <p style={{margin:'0%'}}>Rs.{item.products.price}</p>
                </div>
                <div className="item1" style={{marginTop:'1%',padding:'2%', backgroundColor:'white', border:'solid', borderWidth:'1px', borderColor:'#9C9C9B'}}>
                <button style={{padding:'1%' ,width:'100%', color:'green',border:'white',backgroundColor:'white', margin:'0%'}}> Add To Cart</button>
                </div><br/>
                </div>
                 )}

                </div>
            </div>
            </div>
        )
    }
}


export default Vtab5;
